import { useState } from 'react';

export const useBluetooth = () => {
  const [device, setDevice] = useState<BluetoothDevice | null>(null);
  const [error, setError] = useState<string | null>(null);

  const requestBluetoothDevice = async () => {
    try {
      const selectedDevice = await navigator.bluetooth.requestDevice({
        acceptAllDevices: true,
        optionalServices: ['battery_service'],
      });
      setDevice(selectedDevice);
    } catch (err) {
      console.error(err);
      setError('Bluetooth device request failed');
    }
  };

  return { device, requestBluetoothDevice, error };
};

import { useEffect, useRef, useState } from 'react';

export const useCamera = () => {
  const videoRef = useRef<HTMLVideoElement | null>(null);
  const [stream, setStream] = useState<MediaStream | null>(null);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const startCamera = async () => {
      try {
        const mediaStream = await navigator.mediaDevices.getUserMedia({
          video: true,
        });
        setStream(mediaStream);
        if (videoRef.current) {
          videoRef.current.srcObject = mediaStream;
        }
      } catch (err) {
        console.error(err);
        setError('Camera access denied or not available');
      }
    };

    startCamera();

    return () => {
      stream?.getTracks().forEach((track) => track.stop());
    };
  }, []);

  return { videoRef, stream, error };
};

import { useCallback, useState } from 'react';

type ClipboardState = {
  copiedText: string;
  error: string | null;
  isSupported: boolean;
  copy: (text: string) => Promise<boolean>;
  read: () => Promise<string>;
};

export const useClipboard = (): ClipboardState => {
  const [copiedText, setCopiedText] = useState('');
  const [error, setError] = useState<string | null>(null);

  const isSupported = !!navigator?.clipboard;

  const copy = useCallback(
    async (text: string) => {
      if (!isSupported) {
        setError('Clipboard API not supported');
        return false;
      }

      try {
        await navigator.clipboard.writeText(text);
        setCopiedText(text);
        setError(null);
        return true;
      } catch (error) {
        setError((error as Error).message);
        return false;
      }
    },
    [isSupported]
  );

  const read = useCallback(async () => {
    if (!isSupported) {
      setError('Clipboard API not supported');
      return '';
    }

    try {
      const text = await navigator.clipboard.readText();
      setCopiedText(text);
      setError(null);
      return text;
    } catch (error) {
      setError((error as Error).message);
      return '';
    }
  }, [isSupported]);

  return {
    copiedText,
    error,
    isSupported,
    copy,
    read,
  };
};

import { useEffect, useState } from 'react';

type GeolocationState = {
  coords: GeolocationCoordinates | null;
  timestamp: number | null;
  error: Error | GeolocationPositionError | null;
};

export const useGeolocation = (
  watch: boolean = false,
  options?: PositionOptions
) => {
  const [state, setState] = useState<GeolocationState>({
    coords: null,
    timestamp: null,
    error: null,
  });

  useEffect(() => {
    if (!navigator.geolocation) {
      setState((s) => ({
        ...s,
        error: new Error('Geolocation is not supported'),
      }));
      return;
    }

    const onSuccess = (pos: GeolocationPosition) => {
      setState({
        coords: pos.coords,
        timestamp: pos.timestamp,
        error: null,
      });
    };

    const onError = (err: GeolocationPositionError) => {
      setState((s) => ({ ...s, error: err }));
    };

    let watchId: number;

    if (watch) {
      watchId = navigator.geolocation.watchPosition(
        onSuccess,
        onError,
        options
      );
    } else {
      navigator.geolocation.getCurrentPosition(onSuccess, onError, options);
    }

    return () => {
      if (watch && watchId !== undefined) {
        navigator.geolocation.clearWatch(watchId);
      }
    };
  }, [watch, options]);

  return state;
};

import { useEffect, useState } from 'react';

export const useOnline = (): boolean => {
  const [isOnline, setIsOnline] = useState<boolean>(navigator.onLine);

  useEffect(() => {
    const updateOnline = () => setIsOnline(true);
    const updateOffline = () => setIsOnline(false);

    window.addEventListener('online', updateOnline);
    window.addEventListener('offline', updateOffline);

    return () => {
      window.removeEventListener('online', updateOnline);
      window.removeEventListener('offline', updateOffline);
    };
  }, []);

  return isOnline;
};

import { useState, useEffect } from 'react';

type FetchState<T> = {
  data: T | null;
  loading: boolean;
  error: Error | null;
};

export function useFetch<T = unknown>(url: string, options?: RequestInit) {
  const [state, setState] = useState<FetchState<T>>({
    data: null,
    loading: true,
    error: null,
  });

  useEffect(() => {
    const controller = new AbortController();
    const fetchData = async () => {
      setState({ data: null, loading: true, error: null });

      try {
        const response = await fetch(url, {
          ...options,
          signal: controller.signal,
        });

        if (!response.ok) throw new Error(`HTTP error: ${response.status}`);

        const data = (await response.json()) as T;
        setState({ data, loading: false, error: null });
      } catch (error) {
        if ((error as Error).name !== 'AbortError') {
          setState({ data: null, loading: false, error: error as Error });
        }
      }
    };

    fetchData();

    return () => controller.abort();
  }, [url, JSON.stringify(options)]); // re-run if URL or options change

  return state;
}

