export const chunk = <T>(array: T[], size: number): T[][] => {
  const result: T[][] = [];
  for (let i = 0; i < array.length; i += size) {
    result.push(array.slice(i, i + size));
  }
  return result;
};

export const compact = <T>(
  array: (T | null | undefined | false | '' | 0)[]
): T[] => {
  return array.filter(Boolean) as T[];
};

export const difference = <T>(a: T[], b: T[]): T[] => {
  const setB = new Set(b);
  return a.filter((item) => !setB.has(item));
};

export const groupBy = <T, K extends keyof T>(
  array: T[],
  key: K
): Record<string, T[]> => {
  return array.reduce((acc: Record<string, T[]>, item: T) => {
    const groupKey = String(item[key]);
    if (!acc[groupKey]) {
      acc[groupKey] = [];
    }
    acc[groupKey].push(item);
    return acc;
  }, {});
};

export const intersection = <T>(a: T[], b: T[]): T[] => {
  const setB = new Set(b);
  return a.filter((item) => setB.has(item));
};

export const shuffle = <T>(array: T[]): T[] => {
  const result = [...array];
  for (let i = result.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [result[i], result[j]] = [result[j], result[i]];
  }
  return result;
};

export const union = <T>(...arrays: T[][]): T[] => {
  const set = new Set<T>();
  for (const array of arrays) {
    array.forEach((item) => set.add(item));
  }
  return Array.from(set);
};

export const unique = <T>(array: T[]): T[] => {
  return Array.from(new Set(array));
};

export const copy = (text: string): void => {
  navigator.clipboard
    .writeText(text)
    .then(() => {
      alert('Copied to clipboard!');
    })
    .catch((error) => {
      console.error('Failed to copy: ', error);
    });
};

export const paste = async (): Promise<string> => {
  try {
    const text = await navigator.clipboard.readText();
    return text;
  } catch (error) {
    console.error('Failed to read clipboard contents:', error);
    return '';
  }
};

export const padZero = (n: number) => String(n).padStart(2, '0');

export const formatDate = (date: Date, separator = '-') => {
  const yyyy = date.getFullYear();
  const mm = padZero(date.getMonth() + 1);
  const dd = padZero(date.getDate());
  return `${yyyy}${separator}${mm}${separator}${dd}`;
};

export const formatTime = (date: Date, withSeconds = false) => {
  const hh = padZero(date.getHours());
  const mm = padZero(date.getMinutes());
  const ss = padZero(date.getSeconds());
  return withSeconds ? `${hh}:${mm}:${ss}` : `${hh}:${mm}`;
};

export const formatDateTime = (date: Date) => {
  return `${formatDate(date)} ${formatTime(date, true)}`;
};

export const format = (date: Date) => {
  return {
    date: (separator = '-') => formatDate(date, separator),
    time: (withSeconds = false) => formatTime(date, withSeconds),
    dateTime: () => formatDate(date),
  };
};

const ONE_SECOND = 1000;
const ONE_MINUTE = ONE_SECOND * 60;
const ONE_HOUR = ONE_MINUTE * 60;
const ONE_DAY = ONE_HOUR * 24;

export const diffInTime = (a: Date, b: Date): number => {
  return Math.abs(a.getTime() - b.getTime());
};

export const diffInDays = (a: Date, b: Date): number => {
  return Math.floor(diffInTime(a, b) / ONE_DAY);
};

export const diffInHours = (a: Date, b: Date): number => {
  return Math.floor(diffInTime(a, b) / ONE_HOUR);
};

export const diffInMinutes = (a: Date, b: Date): number => {
  return Math.floor(diffInTime(a, b) / ONE_MINUTE);
};

export const diffInSeconds = (a: Date, b: Date): number => {
  return Math.floor(diffInTime(a, b) / ONE_SECOND);
};

export const diff = (a: Date, b: Date) => {
  return {
    days: () => diffInDays(a, b),
    hours: () => diffInHours(a, b),
    minutes: () => diffInMinutes(a, b),
    seconds: () => diffInSeconds(a, b),
  };
};

