// Run a function only once, then always return the cached result
export const once = <T extends (...args: any[]) => any>(fn: T): T => {
  let called = false;
  let result: ReturnType<T>;
  return ((...args: Parameters<T>): ReturnType<T> => {
    if (!called) {
      result = fn(...args);
      called = true;
    }
    return result;
  }) as T;
};

type FetchOptions = RequestInit & {
  timeout?: number; // Optional timeout in milliseconds
};

export const request = async <T = unknown>(
  url: string,
  options: FetchOptions = {}
): Promise<T> => {
  const { timeout = 8000, ...fetchOptions } = options;
  const controller = new AbortController();
  const timer = setTimeout(() => controller.abort(), timeout);

  try {
    const response = await fetch(url, {
      ...fetchOptions,
      signal: controller.signal,
      headers: {
        'Content-Type': 'application/json',
        ...(fetchOptions.headers || {}),
      },
    });

    clearTimeout(timer);

    if (!response.ok) {
      const errorBody = await response.text();
      throw new Error(
        `HTTP ${response.status} - ${response.statusText}\n${errorBody}`
      );
    }

    const contentType = response.headers.get('content-type');
    if (contentType?.includes('application/json')) {
      return (await response.json()) as T;
    } else {
      return (await response.text()) as unknown as T;
    }
  } catch (error) {
    clearTimeout(timer);

    if (error instanceof DOMException && error.name === 'AbortError') {
      throw new Error('Request timed out');
    } else if (error instanceof TypeError) {
      throw new Error(`Network error or invalid URL: ${error.message}`);
    } else {
      throw error;
    }
  }
};

// Types for the result object with discriminated union
type Success<T> = {
  data: T;
  error: null;
};

type Failure<E> = {
  data: null;
  error: E;
};

type Result<T, E = Error> = Success<T> | Failure<E>;

// Main wrapper function
export async function tryCatch<T, E = Error>(
  promise: Promise<T>
): Promise<Result<T, E>> {
  try {
    const data = await promise;
    return { data, error: null };
  } catch (error) {
    return { data: null, error: error as E };
  }
}

export const commas = (number: number | bigint) => {
  if (typeof number !== 'number' && typeof number !== 'bigint') return '';
  return number.toLocaleString('en-US');
};

export const currency = (
  number: number | bigint,
  currency = 'USD',
  locale = 'en-US'
) => {
  if (typeof number !== 'number' && typeof number !== 'bigint') return '';
  return new Intl.NumberFormat(locale, { style: 'currency', currency }).format(
    number
  );
};

