export const rateLimit = <T extends (...args: any[]) => any>(
  fn: T,
  limitMs: number
): ((...args: Parameters<T>) => Promise<ReturnType<T>>) => {
  let lastCallTime = 0;

  return async function (...args: Parameters<T>): Promise<ReturnType<T>> {
    const now = Date.now();
    const timeSinceLastCall = now - lastCallTime;

    if (timeSinceLastCall < limitMs) {
      await new Promise((res) => setTimeout(res, limitMs - timeSinceLastCall));
    }

    lastCallTime = Date.now();
    return fn(...args);
  };
};

/* eslint-disable @typescript-eslint/no-explicit-any */

export const throttle = <T extends (...args: any[]) => void>(
  fn: T,
  limit: number
): ((...args: Parameters<T>) => void) => {
  let lastCall = 0;

  return (...args: Parameters<T>) => {
    const now = Date.now();
    if (now - lastCall >= limit) {
      lastCall = now;
      fn(...args);
    }
  };
};

// Set a cookie
export const setCookie = (name: string, value: string, days?: number): void => {
  let expires = '';
  if (days) {
    const date = new Date();
    date.setTime(date.getTime() + days * 24 * 60 * 60 * 1000);
    expires = `; expires=${date.toUTCString()}`;
  }
  document.cookie = `${name}=${encodeURIComponent(value)}${expires}; path=/`;
};

// Get a cookie by name
export const getCookie = (name: string): string | null => {
  const regex = new RegExp('(^| )' + name + '=([^;]+)');
  const result = regex.exec(document.cookie);
  return result ? decodeURIComponent(result[2]) : null;
};

// Delete a cookie by name
export const deleteCookie = (name: string): void => {
  setCookie(name, '', -1);
};

interface IDBOptions {
  dbName: string;
  storeName: string;
  version?: number;
}

const openDB = ({
  dbName,
  storeName,
  version = 1,
}: IDBOptions): Promise<IDBDatabase> => {
  return new Promise((resolve, reject) => {
    const request = indexedDB.open(dbName, version);

    request.onupgradeneeded = () => {
      const db = request.result;
      if (!db.objectStoreNames.contains(storeName)) {
        db.createObjectStore(storeName);
      }
    };

    request.onsuccess = () => resolve(request.result);
    request.onerror = () => {
      const err = request.error;
      reject(err instanceof Error ? err : new Error(String(err)));
    };
  });
};

export const setItem = async <T>(
  key: string,
  value: T,
  options: IDBOptions
): Promise<void> => {
  const db = await openDB(options);
  return new Promise((resolve, reject) => {
    const transaction = db.transaction(options.storeName, 'readwrite');
    const store = transaction.objectStore(options.storeName);
    const request = store.put(value, key);

    request.onsuccess = () => resolve();
    request.onerror = () => {
      const err = request.error;
      reject(err instanceof Error ? err : new Error(String(err)));
    };
  });
};

export const getItem = async <T>(
  key: string,
  options: IDBOptions
): Promise<T | null> => {
  const db = await openDB(options);
  return new Promise((resolve, reject) => {
    const transaction = db.transaction(options.storeName, 'readonly');
    const store = transaction.objectStore(options.storeName);
    const request = store.get(key);

    request.onsuccess = () => resolve(request.result ?? null);
    request.onerror = () => {
      const err = request.error;
      reject(err instanceof Error ? err : new Error(String(err)));
    };
  });
};

export const deleteItem = async (
  key: string,
  options: IDBOptions
): Promise<void> => {
  const db = await openDB(options);
  return new Promise((resolve, reject) => {
    const transaction = db.transaction(options.storeName, 'readwrite');
    const store = transaction.objectStore(options.storeName);
    const request = store.delete(key);

    request.onsuccess = () => resolve();
    request.onerror = () => {
      const err = request.error;
      reject(err instanceof Error ? err : new Error(String(err)));
    };
  });
};

export const setLocalStorage = <T>(key: string, value: T): void => {
  if (typeof window === 'undefined') return;

  try {
    localStorage.setItem(key, JSON.stringify(value));
  } catch (error) {
    console.error(`Error setting localStorage key "${key}":`, error);
  }
};

export const getLocalStorage = <T>(key: string): T | null => {
  if (typeof window === 'undefined') return null;

  try {
    const item = localStorage.getItem(key);
    return item ? (JSON.parse(item) as T) : null;
  } catch (error) {
    console.error(`Error getting localStorage key "${key}":`, error);
    return null;
  }
};

export const removeLocalStorage = (key: string): void => {
  if (typeof window === 'undefined') return;

  try {
    localStorage.removeItem(key);
  } catch (error) {
    console.error(`Error removing localStorage key "${key}":`, error);
  }
};

export const clearLocalStorage = (): void => {
  if (typeof window === 'undefined') return;

  try {
    localStorage.clear();
  } catch (error) {
    console.error('Error clearing localStorage:', error);
  }
};

export const LocalStorage = () => {
  return {
    set: setLocalStorage,
    get: getLocalStorage,
    remove: removeLocalStorage,
    clear: clearLocalStorage,
  };
};

